    <div class=\"mt-4 min-vh-100\">
        <div class=\"block-small\">
            {% for message in app.flashes('notice') %}
                <div class=\"alert alert-info\">{{ message }}</div>
            {% endfor %}
            <form method=\"post\">
                <h1 class=\"h3 mb-3 font-weight-normal\">Réinitialiser mon mot de passe</h1>
                <label for=\"inputEmail\">Votre email</label>
                <input type=\"email\" name=\"email\" id=\"inputEmail\" placeholder=\"Votre adresse email\" class=\"form-control\" required autofocus>

                <button class=\"btn btn-lg btn-info btn-block mt-3\" type=\"submit\">
                    Valider
                </button>
                <hr>
                <p class=\"text-center\">Ou souhaitez vous <a href=\"{{ path('app_login') }}\">Vous connecter</a> ?</p>
            </form>
        </div>
    </div>

{% endblock %}
